function getNamespaceInfo(row) {

  var ncLookup = {
    '1': 'nc-3.0.1',
    '2': 'nc-3.0.2',
    '3': 'nc-3.0.3'
  };

  var domainLookup = {
    biometrics: 'Biometrics',
    cbrn: 'Chemical, Biological, Radiological, Nuclear',
    cyfs: 'Children, Youth & Family Services',
    emergencyManagement: 'Emergency Management',
    humanServices: 'Human Services',
    immigration: 'Immigration',
    infrastructureProtection: 'Infrastructure Protection',
    intelligence: 'Intelligence',
    internationalTrade: 'International Trade',
    jxdm: 'Justice',
    maritime: 'Maritime',
    militaryOperations: 'Military Operations',
    screening: 'Screening',
    surfaceTransportation: 'Surface Transportation'
  };

  var namespaceLookup = {
    'aamva_d20': 'aamva_d20',
    'apco_event': 'apco',
    'atf': 'atf',
    'canada_post': 'can',
    'cbrncl': 'cbrncl',
    'ogc': 'ogc',
    'fbi_ndex': 'ndex',
    'have': 'have',
    'mmucc': 'mmucc',
    'fbi_ncic': 'ncic',
    'fbi_ucr': 'ucr',
    'it_': 'itcodes',
    'edxl_have': 'have-codes',
    'core_misc': 'core_misc',
    'unece_rec20': 'unece',
    'de': 'de',
    'census_commodity': 'commodity',
    'jp3-52': 'jp3-52',
    'pmise_sar': 'sar',
    'dea_ctlsub': 'dea',
    'nlets': 'nlets',
    'jc3iedm': 'jc3iedm',
    'edxl_rm': 'edxl_rm',
    'fips_5-2': 'fips_5-2',
    'nga_datum': 'nga',
    'occs_facility': 'occs',
    'cap': 'cap',
    'dod_jcs-pub2.0': 'dod_jcs-pub2.0',
    'fips_10-4': 'fips_10-4',
    'nga_genc': 'genc',
    'hl7': 'hl7',
    'iso_3166-1': 'iso_3166',
    'iso_4217': 'iso_4217',
    'usps_states': 'usps',
    'xCard': 'xCard',
    'census_uscounty': 'census',
    'dol_soc': 'dol',
    'dot_hazmat': 'hazmat',
    'fips_6-4': 'fips_6-4',
    'iso_639-3': 'iso639-3',
    'proxy': 'niem-xs',
    'geospatial': 'geo',
    'structures': 'structures',
    'edxl-cap': 'edxl-cap',
    'edxl-de': 'edxl-de',
    'edxl-have': 'edxl-have',
    'gml': 'gml',
    'iso': 'iso',
    'ols': 'ols',
    'xlink': 'xlink',
    'biometrics': 'biom',
    'cbrn': 'cbrn',
    'cyfs': 'cyfs',
    'emergencyManagement': 'em',
    'humanServices': 'hs',
    'immigration': 'im',
    'infrastructureProtection': 'ip',
    'intelligence': 'intel',
    'internationalTrade': 'it',
    'jxdm': 'j',
    'maritime': 'm',
    'militaryOperations': 'mo',
    'screening': 'scr',
    'surfaceTransportation': 'st',
    'Core': 'nc'
  }

  var path = row.get("fileAbsolutePath");
  var pathSlash = path.replace(/\\/g,"/");
  var namespaceType;
  var namespace;
  var domain;
  var externalStandard;
  var otherNamespace;
  var namespacePriority;
  var namespacePrefix;

  // Determine if file is in domains dir or niem-core
  if (path.indexOf('domains') > -1) {
    namespaceType = 'domain';

    // Loop through domainLookup object to find match
    Object.keys(domainLookup).forEach(function(key) {
      if (path.indexOf(key) > -1) {
        domain = domainLookup[key];
        namespacePrefix = namespaceLookup[key];
      }
    });

    namespace = domain;
    namespacePriority = 2;

  } else if ( path.indexOf('niem-core') > -1) {
    namespaceType = 'domain';
    domain = 'Core';
    namespace = domain;
    namespacePriority = 1;

    if ( path.indexOf('3.0/1') > -1 ) {
      namespacePrefix = ncLookup['1'];
    } else if ( path.indexOf('3.0/2') > -1 ) {
      namespacePrefix = ncLookup['2'];
    } else if ( path.indexOf('3.0/3') > -1 ) {
      namespacePrefix = ncLookup['3'];
    } else {
      namespacePrefix = namespaceLookup[namespace];
    }
  } else if ( path.indexOf('codes') > -1 ) {
    namespaceType = 'externalStandard';
    externalStandard = pathSlash.split('codes/')[1].split('/')[0];
    namespace = externalStandard;
    namespacePriority = 3;

    // Handle one-off cases, such as nga_genc
    if ( path.indexOf('nga_genc/3.0/1') > -1 ) {
      namespacePrefix = 'genc-3.0.1';
    } else {
      namespacePrefix = namespaceLookup[namespace];
    }
  } else if (path.indexOf('external') > -1) {
    namespaceType = 'externalStandard';
    var standard = pathSlash.split('external/')[1].split('/')[0];
    namespacePriority = 3;

    // Handle ogc sub-standards such as gml, iso, ols, and xlink
    if ( standard === 'ogc') {
      externalStandard = pathSlash.split('external/ogc/')[1].split('/')[0];
    } else {
      externalStandard = standard;
    }

    namespace = externalStandard;
    namespacePrefix = namespaceLookup[namespace];

  } else if( path.indexOf('adapters') > -1) {
    namespaceType = 'otherNamespace';
    otherNamespace = pathSlash.split('adapters/')[1].split('/')[0];
    namespace = otherNamespace;
    namespacePrefix = namespaceLookup[namespace];
    namespacePriority = 4;
  } else {
    namespaceType = 'otherNamespace';
    otherNamespace = pathSlash.split('niem-xsd/')[1].split('/')[0];
    namespace = otherNamespace;
    namespacePrefix = namespaceLookup[namespace];
    namespacePriority = 4;
  }

  if (domain) {
    row.put("domain", domain);
  } else if (externalStandard) {
    row.put("externalStandard", externalStandard);
  } else if (otherNamespace) {
    row.put("otherNamespace", otherNamespace);
  }

  row.put("namespace", namespace);
  row.put("namespaceType", namespaceType);
  row.put("namespacePrefix", namespacePrefix);
  row.put("namespacePriority", namespacePriority);

  return row;

};


function getContentStyle(row) {

  var contentStyle;
  var complexContent = row.get("complexContent");
  var simpleContent = row.get("simpleContent")

  if (complexContent) {
    contentStyle = 'Complex with Complex Content (CCC)';
  } else if (simpleContent) {
    contentStyle = 'Complex with Simple Content (CSC)';
  }

  row.put("contentStyle", contentStyle);

  return row;
};


function getIsBG(row) {

  var isBG;
  var elementName = row.get("name");

  // Check if element name contains a BG disqualifying word
  if ( elementName.indexOf("Augmentation") >= 0 || elementName.indexOf("Association") >= 0 ) {
    isBG = 0;
  } else {
    isBG = 1;
  }

  row.put("isBG", isBG);

  return row;
};


function getFieldLength(row) {
  var name = row.get("name");
  var nameLength = name.length;
  row.put("nameLength", nameLength);
  return row;
}
